import React, { PureComponent } from 'react'
import { baseUrl } from '../constants'

export default class Main extends PureComponent {
  state = { file: null }

  handleSubmit = e => {
    e.preventDefault()
    const { file } = this.state
    console.log('TCL: Main -> file', file)

    const params = {
      title: e.currentTarget.title.value,
      attribution: e.currentTarget.attribution.value,
      notes: e.currentTarget.notes.value,
      live: e.currentTarget.live.value,
      url: file
    }
    this.handlePostImage(params)
  }

  handlePostImage = params => {
    fetch(baseUrl + '/images', {
      method: 'POST',
      body: JSON.stringify(params),
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      }
    })
      .then(res => res.json())
      .then(data => {
        console.log('TCL: App -> data', data)
      })
  }

  handleChange = e => {
    if (e.target.files.length > 0) {
      const reader = new FileReader()

      const file = e.target.files[0]
      console.log('TCL: Main -> file', file)

      reader.onloadend = () => {
        this.setState({ file: reader.result })
      }

      reader.readAsDataURL(file)
    }
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <input type="text" name="title" placeholder="Title" />
        <input type="text" name="attribution" placeholder="Attribution" />
        <input type="text" name="notes" placeholder="Notes" />
        <label>
          Live
          <input type="checkbox" name="live" placeholder="Live" />
        </label>
        <input
          onChange={this.handleChange}
          name="url"
          type="file"
          accept=".jpg, .jpeg, .png, .gif"
        />
        <button type="submit">Submit Image</button>
      </form>
    )
  }
}
