import React, { PureComponent } from 'react'
import { themeColors } from '../../constants'
import styles from './styles'
import { styled } from 'styletron-react'

import { baseUrl } from '../../constants'

export default class Orders extends PureComponent {
  renderOrderItems = (order, orderItem) => {
    const pizzaTypes = order.pizza_types
    console.log('TCL: Orders -> pizzaTypes', pizzaTypes)

    const pizzaType = pizzaTypes.find(pt => pt.id === orderItem.pizza_type_id)

    return { ...orderItem, name: pizzaType.name, price: pizzaType.price }
  }

  render() {
    const { orders } = this.props
    console.log('TCL: Orders -> render -> orders', orders)

    return (
      <div style={{ margin: 'auto', width: '1000px' }}>
        <table
          style={{
            margin: 'auto',
            padding: '3rem',
            background: themeColors.tertiaryColor,
            color: themeColors.quinaryColor,
            borderRadius: '.25rem'
          }}
        >
          <tr>
            <th style={{ width: '100px', padding: '.5rem' }}>First Name</th>
            <th style={{ width: '100px', padding: '.5rem' }}>Last Name</th>
            <th style={{ width: '400px', padding: '.5rem' }}>Address</th>
            <th style={{ width: '125px', padding: '.5rem' }}>Item</th>
            <th style={{ width: '50px', padding: '.5rem' }}>Price</th>
            <th style={{ width: '50px', padding: '.5rem' }}>Quantity</th>
          </tr>
          {orders.map((order, i) => {
            console.log(
              'TCL: Orders -> render -> order.pizza_types',
              order.pizza_types
            )
            return (
              <tr key={i}>
                <td style={{ width: '100px', textAlign: 'center' }}>
                  {order.user.first_name}
                </td>
                <td style={{ width: '100px', textAlign: 'center' }}>
                  {order.user.last_name}
                </td>
                <td style={{ width: '400px', textAlign: 'center' }}>
                  {order.address.full_address}
                </td>
                {order.order_items.map(orderItem => {
                  const fullOrderItem = this.renderOrderItems(order, orderItem)
                  return (
                    <>
                      <td style={{ width: '125px', textAlign: 'center' }}>
                        {' '}
                        {fullOrderItem.name}
                      </td>
                      <td style={{ width: '50px', textAlign: 'center' }}>
                        {fullOrderItem.price}
                      </td>
                      <td style={{ width: '50px', textAlign: 'center' }}>
                        {orderItem.quantity.toString()}
                      </td>
                    </>
                  )
                })}
              </tr>
            )
          })}
        </table>
      </div>
    )
  }
}
