import { screenSizes, colorPalletes } from '../../constants'
import { lighten, darken } from '../../utils/color'
import { transitions } from '../../constants'

const styles = {
  navbar: {
    position: 'fixed',
    height: '4rem',
    width: '100%',
    backgroundColor: colorPalletes[2].primaryColor,
    zIndex: 1001,
    marginLeft: 'auto',
    marginRight: 'auto'
  },
  navbarContent: {
    position: 'relative',
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    height: '100%',
    paddingLeft: '5rem',
    paddingRight: '5rem'
  },
  secondaryButton: {
    borderColor: colorPalletes[2].secondaryColor,
    backgroundColor: colorPalletes[2].secondaryColor,
    color: colorPalletes[2].quinaryColor,
    ':hover': {
      backgroundColor: lighten(colorPalletes[2].secondaryColor, 0.3),
      borderColor: lighten(colorPalletes[2].secondaryColor, 0.3)
    },

    ':focus': {
      backgroundColor: darken(colorPalletes[2].secondaryColor, 0.1),
      borderColor: darken(colorPalletes[2].secondaryColor, 0.1)
    },

    ':active': {
      backgroundColor: darken(colorPalletes[2].secondaryColor, 0.2),
      borderColor: darken(colorPalletes[2].secondaryColor, 0.2)
    }
  }
}

export default styles
