import { lighten, darken } from '../../utils/color'
import { transitions } from '../../constants'
import { colorPalletes } from '../../constants'

const styles = {
  button: {
    display: 'inline-block',
    fontSize: '1rem',
    position: 'relative',
    textAlign: 'center',
    borderRadius: '4px',
    cursor: 'pointer',
    whiteSpace: 'nowrap',
    touchAction: 'manipulation',
    userSelect: 'none',
    outline: '0',
    padding: '0.25rem 1.25rem',
    transition: `all ${transitions.standardTiming} ${transitions.standardCubic}`
  },
  primaryButton: ({ $themeNumber }) => {
    return {
      borderColor: colorPalletes[$themeNumber].primaryColor,
      backgroundColor: colorPalletes[$themeNumber].primaryColor,
      color: themeColors.quinaryColor,
      ':hover': {
        backgroundColor: lighten(colorPalletes[$themeNumber].primaryColor, 0.3),
        borderColor: lighten(colorPalletes[$themeNumber].primaryColor, 0.3)
      },

      ':focus': {
        backgroundColor: darken(colorPalletes[$themeNumber].primaryColor, 0.1),
        borderColor: darken(colorPalletes[$themeNumber].primaryColor, 0.1)
      },

      ':active': {
        backgroundColor: darken(colorPalletes[$themeNumber].primaryColor, 0.2),
        borderColor: darken(colorPalletes[$themeNumber].primaryColor, 0.2)
      }
    }
  },

  primaryButton: {
    borderColor: themeColors.primaryColor,
    backgroundColor: themeColors.primaryColor,
    color: themeColors.quinaryColor,
    ':hover': {
      backgroundColor: lighten(themeColors.primaryColor, 0.3),
      borderColor: lighten(themeColors.primaryColor, 0.3)
    },

    ':focus': {
      backgroundColor: darken(themeColors.primaryColor, 0.1),
      borderColor: darken(themeColors.primaryColor, 0.1)
    },

    ':active': {
      backgroundColor: darken(themeColors.primaryColor, 0.2),
      borderColor: darken(themeColors.primaryColor, 0.2)
    }
  },
  secondaryButton: {
    borderColor: themeColors.secondaryColor,
    backgroundColor: themeColors.secondaryColor,
    color: themeColors.primaryColor,
    ':hover': {
      backgroundColor: lighten(themeColors.secondaryColor, 0.3),
      borderColor: lighten(themeColors.secondaryColor, 0.3)
    },

    ':focus': {
      backgroundColor: darken(themeColors.secondaryColor, 0.1),
      borderColor: darken(themeColors.secondaryColor, 0.1)
    },

    ':active': {
      backgroundColor: darken(themeColors.secondaryColor, 0.2),
      borderColor: darken(themeColors.secondaryColor, 0.2)
    }
  }
}

export default styles
