import { colorPalletes } from '../../../constants'
import { darken } from '../../../utils/color'

const styles = {
  dropdownItemContainer: ({ $themeNumber }) => {
    return {
      ':visited': {
        color: 'inherit'
      },

      ':hover': {
        color: colorPalletes[$themeNumber].textColor,
        backgroundColor: colorPalletes[$themeNumber].tertiaryColor
      },

      ':active': {
        color: colorPalletes[$themeNumber].textColor,
        backgroundColor: darken(colorPalletes[$themeNumber].tertiaryColor, 0.1)
      }
    }
  },

  dropdownItemContent: {
    overflow: 'hidden',
    textOverflow: 'ellipsis'
  }
}

export default styles
