import React, { PureComponent } from 'react'
import base64Img from 'base64-img'

export default class Main extends PureComponent {
  handleSubmit = e => {
    e.preventDefault()
    const params = {
      title: e.currentTarget.title.value,
      attribution: e.currentTarget.attribution.value,
      notes: e.currentTarget.notes.value,
      live: e.currentTarget.live.value,
      url: e.currentTarget.url.value
    }
    console.log(params)
    console.log(
      base64Img.base64(e.currentTarget.url.value, function(err, data) {})
    )
  }
  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <input type="text" name="title" placeholder="Title" />
        <input type="text" name="attribution" placeholder="Attribution" />
        <input type="text" name="notes" placeholder="Notes" />
        <label>
          Live
          <input type="checkbox" name="live" placeholder="Live" />
        </label>
        <input name="url" type="file" accept=".jpg, .jpeg, .png, .gif" />
        <button type="submit">Login</button>
      </form>
    )
  }
}
