import { screenSizes, colors } from '../../constants'

const styles = {
  form: {
    margin: '2rem auto'
  },
  row: {
    display: 'flex',
    justifyContent: 'space-between',
    margin: '1rem'
  },
  field: {},
  upload: {
    display: 'inline-block',
    fontSize: '1rem',
    borderColor: primaryColor,
    backgroundColor: primaryColor,
    color: 'white',
    position: 'relative',
    textAlign: 'center',
    borderRadius: '4px',
    cursor: 'pointer',
    whiteSpace: 'nowrap',
    touchAction: 'manipulation',
    userSelect: 'none',
    outline: '0',
    transition: `all ${standardTiming} ${standardCubic}`,

    ':hover': {
      backgroundColor: lighten(primaryColor, 0.3),
      borderColor: lighten(primaryColor, 0.3)
    },

    ':focus': {
      backgroundColor: darken(primaryColor, 0.1),
      borderColor: darken(primaryColor, 0.1)
    },

    ':active': {
      backgroundColor: darken(primaryColor, 0.2),
      borderColor: darken(primaryColor, 0.2)
    }
  }
}

export default styles
