import { colorPalletes } from '../../../constants'
import { darken } from '../../../utils/color'

const styles = {
  dropdownItemContainer: ({ $themeNumber }) => {
    return {
      paddingLeft: '2rem',
      paddingRight: '2rem',
      paddingTop: '0.5rem',
      paddingBottom: '0.5rem',
      display: 'flex',
      alignItems: 'center',
      color: colorPalletes[$themeNumber].secondaryColor,
      textDecoration: 'none',

      ':visited': {
        color: 'inherit'
      },

      ':hover': {
        color: colorPalletes[$themeNumber].textColor,
        backgroundColor: colorPalletes[$themeNumber].tertiaryColor
      },

      ':active': {
        color: colorPalletes[$themeNumber].textColor,
        backgroundColor: darken(colorPalletes[$themeNumber].tertiaryColor, 0.1)
      }
    }
  },

  dropdownItemContent: {
    overflow: 'hidden',
    textOverflow: 'ellipsis',
    whiteSpace: 'nowrap',
    fontSize: '0.875rem'
  },

  dropdownItemIcon: {
    position: 'absolute',
    left: '0.5rem',
    height: '1rem',
    width: '1rem'
  }
}

export default styles
