import { themeColors } from '../../../constants'
import { darken } from '../../../utils/color'

const styles = {
  dropdownItemContainer: ({ $themeNumber }) => {
    return {
      position: 'fixed',
      height: '4rem',
      width: '100%',
      backgroundColor: colorPalletes[$themeNumber].primaryColor,
      zIndex: 1001,
      marginLeft: 'auto',
      marginRight: 'auto'
    }
  },

  dropdownItemContent: {
    overflow: 'hidden',
    textOverflow: 'ellipsis'
  }
}

export default styles
