import { themeColors } from '../../constants'

const styles = {
  form: {
    margin: '2rem auto',
    width: '500px'
  },
  table: {
    border: `1px solid ${themeColors.secondaryColor}`,
    width: '100%'
  },
  tableHeader: {
    borderBottom: `1px solid ${themeColors.secondaryColor}`
  },
  tableRow: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '100%'
  },
  tableCell: {
    width: '200px',
    textAlign: 'center'
  },
  row: {
    display: 'flex',
    margin: '1rem'
  },
  field: {
    margin: '.25rem 1rem'
  }
}

export default styles
