import React, { PureComponent } from 'react'
import AdditionalOrderItem from './AdditionalOrderItem'
import { themeColors, textColors } from '../../constants'
import { formatDate } from '../../utils/date'
import OrderItem from './OrderItem'

export default class Orders extends PureComponent {
  renderOrderItems = (order, orderItem) => {
    const pizzaTypes = order.pizza_types
    console.log('TCL: Orders -> pizzaTypes', pizzaTypes)

    const pizzaType = pizzaTypes.find(pt => pt.id === orderItem.pizza_type_id)

    return { ...orderItem, name: pizzaType.name, price: pizzaType.price }
  }

  render() {
    const { orders } = this.props

    return (
      <>
        <div
          style={{
            width: '1000px',
            margin: '1rem auto 0 auto'
          }}
        >
          <table
            style={{
              padding: '0 3rem 3rem 3rem',
              background: themeColors.tertiaryColor,
              color: themeColors.quinaryColor,
              borderRadius: '.25rem .25 0 0'
            }}
          >
            <tr style={{ height: '2rem' }}>
              <th
                style={{
                  textAlign: 'center',
                  width: '50px',
                  padding: '.5rem',
                  borderBottom: `1px solid ${themeColors.quaternaryColor}`
                }}
              >
                Date
              </th>
              <th
                style={{
                  textAlign: 'center',
                  width: '100px',
                  borderBottom: `1px solid ${themeColors.quaternaryColor}`
                }}
              >
                First Name
              </th>
              <th
                style={{
                  textAlign: 'center',
                  width: '100px',
                  padding: '.5rem',
                  borderBottom: `1px solid ${themeColors.quaternaryColor}`
                }}
              >
                Last Name
              </th>
              <th
                style={{
                  textAlign: 'center',
                  width: '400px',
                  padding: '.5rem',
                  borderBottom: `1px solid ${themeColors.quaternaryColor}`
                }}
              >
                Address
              </th>
              <th
                style={{
                  textAlign: 'center',
                  width: '50px',
                  padding: '.5rem',
                  borderBottom: `1px solid ${themeColors.quaternaryColor}`
                }}
              >
                Discount
              </th>
              <th
                style={{
                  textAlign: 'center',
                  width: '50px',
                  padding: '.5rem',
                  borderBottom: `1px solid ${themeColors.quaternaryColor}`
                }}
              >
                Tax
              </th>
              <th
                style={{
                  textAlign: 'center',
                  width: '50px',
                  padding: '.5rem',
                  borderBottom: `1px solid ${themeColors.quaternaryColor}`
                }}
              >
                Total
              </th>
              <th
                style={{
                  textAlign: 'center',
                  width: '125px',
                  padding: '.5rem',
                  borderBottom: `1px solid ${themeColors.quaternaryColor}`
                }}
              >
                Item
              </th>
              <th
                style={{
                  textAlign: 'center',
                  width: '50px',
                  padding: '.5rem',
                  borderBottom: `1px solid ${themeColors.quaternaryColor}`
                }}
              >
                Price
              </th>
              <th
                style={{
                  textAlign: 'center',
                  width: '50px',
                  padding: '.5rem',
                  borderBottom: `1px solid ${themeColors.quaternaryColor}`
                }}
              >
                Quantity
              </th>
            </tr>
          </table>
        </div>
        <div
          style={{
            margin: '0 auto',
            width: '1000px',
            height: '300px',
            overflow: 'scroll'
          }}
        >
          <table
            style={{
              padding: '0 3rem 3rem 3rem',
              background: themeColors.tertiaryColor,
              color: themeColors.quinaryColor,
              borderRadius: '0 0 .25rem .25'
            }}
          >
            {orders.map((order, i) => {
              const currentDate = new Date()
              const orderDate = new Date(order.date_time)
              const color =
                currentDate - orderDate > 46767835
                  ? textColors.textMuted
                  : textColors.textPrimary

              return (
                <>
                  <tr key={i} style={{ height: '2rem', color: `${color}` }}>
                    {' '}
                    <td
                      style={{
                        width: '50px',
                        textAlign: 'center'
                      }}
                    >
                      {formatDate(orderDate)}
                    </td>
                    <td style={{ width: '100px', textAlign: 'center' }}>
                      {order.user.first_name}
                    </td>
                    <td style={{ width: '100px', textAlign: 'center' }}>
                      {order.discount}
                    </td>
                    <td style={{ width: '400px', textAlign: 'center' }}>
                      {order.address.full_address}
                    </td>
                    <td style={{ width: '50px', textAlign: 'center' }}>
                      {order.discount * 100}%
                    </td>
                    <td style={{ width: '50px', textAlign: 'center' }}>
                      ${order.tax}
                    </td>
                    <td style={{ width: '50px', textAlign: 'center' }}>
                      ${order.total}
                    </td>
                    {order.order_items.map((orderItem, i) => {
                      const fullOrderItem = this.renderOrderItems(
                        order,
                        orderItem
                      )
                      if (i === 0)
                        return <OrderItem fullOrderItem={fullOrderItem} i={i} />
                    })}
                  </tr>
                  {order.order_items.map((orderItem, i) => {
                    const fullOrderItem = this.renderOrderItems(
                      order,
                      orderItem
                    )
                    return (
                      <AdditionalOrderItem
                        fullOrderItem={fullOrderItem}
                        i={i}
                        color={color}
                      />
                    )
                  })}
                </>
              )
            })}
          </table>
        </div>
      </>
    )
  }
}
