import React, { PureComponent } from 'react'
import { themeColors } from '../../constants'
import styles from './styles'
import { styled } from 'styletron-react'

import { baseUrl } from '../../constants'

export default class Orders extends PureComponent {
  renderOrderItems = (order, orderItem) => {
    const pizzaTypes = order.pizza_types
    console.log('TCL: Orders -> pizzaTypes', pizzaTypes)

    const pizzaType = pizzaTypes.find(pt => pt.id === orderItem.pizza_type_id)

    return { ...orderItem, name: pizzaType.name, price: pizzaType.price }
  }

  render() {
    const { orders } = this.props
    console.log('TCL: Orders -> render -> orders', orders)

    return (
      <div style={{ margin: 'auto', width: '1000px' }}>
        {orders.map((order, i) => {
          console.log(
            'TCL: Orders -> render -> order.pizza_types',
            order.pizza_types
          )
          return (
            <div style={{ display: 'flex', width: '100%' }} key={i}>
              <span style={{ textAlign: 'center' }}>
                {order.user.first_name}
              </span>
              <span>{order.user.last_name}</span>
              <span style={{ textAlign: 'center' }}>
                {' '}
                {order.address.full_address}
              </span>
              {order.order_items.map(orderItem => {
                const fullOrderItem = this.renderOrderItems(order, orderItem)
                return (
                  <>
                    <span style={{ textAlign: 'center' }}>
                      {' '}
                      {fullOrderItem.name}
                    </span>
                    <span style={{ textAlign: 'center' }}>
                      {fullOrderItem.price}{' '}
                    </span>
                    <span style={{ textAlign: 'center' }}>
                      {orderItem.quantity.toString()}
                    </span>
                  </>
                )
              })}
            </div>
          )
        })}
      </div>
    )
  }
}
