import React, { PureComponent } from 'react'
import { themeColors } from '../../constants'
import styles from './styles'
import { styled } from 'styletron-react'
import Button from '../Button'
import Dropdown from '../Dropdown'

const Form = styled('form', styles.form)
const Field = styled('label', styles.field)
const Row = styled('div', styles.row)

export default class OrderForm extends PureComponent {
  state = {
    order: []
  }

  componentDidMount() {
    let token = localStorage.getItem('token')
    if (token) {
      fetch(baseUrl + '/orders', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(orders => {
          console.log(orders)
          this.setState({ user: orders })
        })
        .catch(e => console.error(e))
    }
  }

  handleSelectPizzaType = pizzaType => {
    const { order } = this.state
    const updatedOrder = order.push({ pizzaType, quantity: 1 })
    this.setState({
      order: updatedOrder
    })
  }
  render() {
    const { handlePlaceOrder, handleCancelOrder } = this.props
    return (
      <Form onSubmit={handlePlaceOrder}>
        <Row>
          <Field>
            <Dropdown
              Component={
                <div style={{ color: themeColors.primaryColor }}>
                  Select Pizza Type
                </div>
              }
            >
              <Dropdown.Item
                onClick={() => this.handleSelectPizzaType('margherita')}
              >
                Theme 1
              </Dropdown.Item>
              <Dropdown.Item
                onClick={() => this.handleSelectPizzaType('pepperoni')}
              >
                Theme 2
              </Dropdown.Item>
              <Dropdown.Item
                onClick={() => this.handleSelectPizzaType('chronicle_special')}
              >
                Theme 3
              </Dropdown.Item>
            </Dropdown>
          </Field>
          <Field>
            Attribution:
            <input
              style={{ marginLeft: '.5rem' }}
              type="text"
              name="attribution"
              placeholder="Attribution"
            />
          </Field>
        </Row>
        <Row>
          <Field>
            Notes:
            <input
              style={{ marginLeft: '.5rem' }}
              type="text"
              name="notes"
              placeholder="Notes"
            />
          </Field>
          <Field>
            Live:
            <input
              style={{ marginLeft: '.5rem' }}
              type="checkbox"
              name="live"
              placeholder="Live"
            />
          </Field>
        </Row>
        <Row style={{ justifyContent: 'center' }}>
          <Field />
        </Row>
        <Row style={{ justifyContent: 'space-between' }}>
          <Button
            outline={true}
            color={themeColors.primaryColor}
            onClick={handleCancelOrder}
          >
            Cancel Order
          </Button>

          <Button color={themeColors.primaryColor} type={'submit'}>
            Submit Order
          </Button>
        </Row>
      </Form>
    )
  }
}
