import React, { PureComponent } from 'react'
import { themeColors } from '../../constants'
import styles from './styles'
import { styled } from 'styletron-react'
import Button from '../Button'
import Dropdown from '../Dropdown'
import { baseUrl } from '../../constants'

const Form = styled('form', styles.form)
const TableHeader = styled('label', styles.tableHeader)
const Field = styled('label', styles.field)
const Row = styled('div', styles.row)

export default class OrderForm extends PureComponent {
  state = {
    order: [],
    pizzaTypes: []
  }

  componentDidMount() {
    let token = localStorage.getItem('token')
    if (token) {
      fetch(baseUrl + '/pizza_types', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(pizzaTypes => {
          console.log(pizzaTypes)
          this.setState({ pizzaTypes })
        })
        .catch(e => console.error(e))
    }
  }

  handleSelectPizzaType = pizzaType => {
    const { order } = this.state
    const newItem = {
      name: pizzaType.name,
      price: pizzaType.price,
      quantity: 1
    }

    const updatedOrder = [...order, newItem]
    this.setState({
      order: updatedOrder
    })
  }
  render() {
    const { pizzaTypes, order } = this.state
    const { handlePlaceOrder, handleCancelOrder } = this.props
    return (
      <Form onSubmit={handlePlaceOrder}>
        <Row style={{ justifyContent: 'center' }}>
          <Field>
            <Dropdown
              Component={
                <Button color={themeColors.tertiaryColor}>
                  Select Pizza Type
                </Button>
              }
            >
              {pizzaTypes.map((pt, i) => {
                return (
                  <Dropdown.Item
                    key={i}
                    onClick={() => this.handleSelectPizzaType(pt)}
                  >
                    {pt.name}
                  </Dropdown.Item>
                )
              })}
            </Dropdown>
          </Field>
        </Row>
        <Row>
          <div
            style={{
              display: 'flex',
              flexDirection: 'row',
              justifyContent: 'space-between',
              width: '100%'
            }}
          >
            <TableHeader>Pizza</TableHeader>
            <TableHeader>Price</TableHeader>
            <TableHeader>Quantity</TableHeader>
          </div>
          {order.map(orderItem => {
            return (
              <div
                style={{
                  display: 'flex',
                  flexDirection: 'row',
                  justifyContent: 'space-between',
                  width: '100%'
                }}
              >
                <div>{orderItem.name}</div>
                <div>${orderItem.price}</div>
                <div>{orderItem.quantity}</div>
              </div>
            )
          })}
        </Row>
        <Row style={{ justifyContent: 'center' }}>
          <Field />
        </Row>
        <Row style={{ justifyContent: 'space-between' }}>
          <Button
            outline={true}
            color={themeColors.primaryColor}
            onClick={handleCancelOrder}
          >
            Cancel Order
          </Button>

          <Button color={themeColors.primaryColor} type={'submit'}>
            Submit Order
          </Button>
        </Row>
      </Form>
    )
  }
}
